package com.gms.controller;

import java.io.FileOutputStream;
import java.io.OutputStream;
import java.util.Map;
import java.util.UUID;

import javax.annotation.Resource;

import org.apache.shiro.codec.Base64;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.ResponseBody;

import com.gms.annoation.NeedAuth;
import com.gms.entity.jxc.User;
import com.gms.service.jxc.UserService;

@NeedAuth
public class FileUploadController extends BaseAppController {
	//文件存储路径
	@Value(value = "${picuploadPath}")
	private String picturePath;
	
	@Value(value = "${picPath}")
	private String picPath;
	
	@Resource
	private UserService userService;
	
	// 修改头像
	@RequestMapping("/uploadImg")
	@ResponseBody
	public Map<String, Object> uploadImage(String baseStr) {
		/**
		 * 1、获取图片的Base64 2、把图片存在本地 3、获取图片的访问链接 4、把链接存到用户表中
		 */
		String randomFileName = UUID.randomUUID().toString().replaceAll("-", "") + ".jpg"; //随机文件名
		String truePath = picturePath + randomFileName;
		String returnPath = picPath + randomFileName;
		boolean result = generateImage(baseStr, truePath);
		if (result) {
			// 4、把链接存到用户表中
			User user = getUser();
			user.setImgUrl(returnPath);
			userService.save(user);
			return success(returnPath);
		}
		return error("文件上传失败");
	}
	
	// 修改头像
	@RequestMapping("/uploadGoodImg")
	public Map<String, Object> uploadGoodImg(String baseStr) {
		/**
		 * 1、获取图片的Base64 2、把图片存在本地 3、获取图片的访问链接 4、把链接存到用户表中
		 */	
		String randomFileName = UUID.randomUUID().toString().replaceAll("-", "") + ".jpg"; //随机文件名
		String truePath = picturePath + randomFileName;
		String returnPath = picPath + randomFileName;
		boolean result = generateImage(baseStr, truePath);
		if (result) {
			return success(returnPath);
		}
		return error("文件上传失败");
	}
	
	
	// base64字符串转化成图片
	public static boolean generateImage(String imgStr, String imgFilePath) {
		//
		if (imgStr == null) // 图像数据为空
			return false;

		try {
			// Base64解码
			imgStr = imgStr.replace("data:image/png;base64,", "").replace("base64,", "");
			byte[] b = Base64.decode(imgStr);
			for (int i = 0; i < b.length; ++i) {
				if (b[i] < 0) {// 调整异常数据
					b[i] += 256;
				}
			}
			OutputStream out = new FileOutputStream(imgFilePath);
			out.write(b);
			out.flush();
			out.close();
			return true;
		} catch (Exception e) {
			return false;
		}
	}

	public static void main(String[] args) {
		String strImg = "data:image/png;base64,";
		String path = "E:\\workspace\\cms\\jxc-web-project\\gms-maven-management\\gms-app\\src\\main\\webapp\\pic\\";
		String picName =  UUID.randomUUID().toString().replaceAll("-", "") + ".jpg";
		generateImage(strImg, path + picName);
		System.out.println("localhost:8080/pic/" + picName);
	}
}
